import React, { useContext, useState } from 'react';
import { CartContext } from '../context/CartContext';
import {
  TextField,
  Button,
  Typography,
  List,
  ListItem,
  ListItemText,
  Divider
} from '@mui/material';

const Checkout = () => {
  const { cart } = useContext(CartContext);
  const [form, setForm] = useState({
    location: '',
    mobile: '',
    audi: '',
    seat: ''
  });

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async () => {
    if (!form.location || !form.mobile || !form.audi || !form.seat) {
      alert('Please fill in all details.');
      return;
    }

    const order = {
      items: cart,
      ...form,
      time: new Date().toISOString()
    };

    try {
      const res = await fetch('http://localhost:5001/orders', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(order)
      });

      if (res.ok) {
        alert('✅ Order placed successfully!');
        // Optional: Clear cart or navigate back
      } else {
        alert('❌ Failed to place order.');
      }
    } catch (error) {
      console.error(error);
      alert('Error occurred while placing order.');
    }
  };

  return (
    <div style={{ padding: 20, maxWidth: 600, margin: '0 auto' }}>
      <Typography variant="h5" gutterBottom>
        Checkout
      </Typography>

      <List>
        {cart.map((item, index) => (
          <ListItem key={index}>
            <ListItemText primary={item.name} secondary={`₹${item.price}`} />
          </ListItem>
        ))}
      </List>
      <Divider style={{ margin: '16px 0' }} />

      <TextField
        label="Location"
        name="location"
        fullWidth
        margin="normal"
        value={form.location}
        onChange={handleChange}
      />
      <TextField
        label="Mobile Number"
        name="mobile"
        fullWidth
        margin="normal"
        value={form.mobile}
        onChange={handleChange}
      />
      <TextField
        label="Audi Number"
        name="audi"
        fullWidth
        margin="normal"
        value={form.audi}
        onChange={handleChange}
      />
      <TextField
        label="Seat Number"
        name="seat"
        fullWidth
        margin="normal"
        value={form.seat}
        onChange={handleChange}
      />

      <Button
        variant="contained"
        color="primary"
        fullWidth
        onClick={handleSubmit}
        style={{ marginTop: '20px' }}
      >
        Place Order
      </Button>
    </div>
  );
};

export default Checkout;
