import React, { useEffect, useState, useContext } from 'react';
import {
  Card,
  CardContent,
  CardMedia,
  Typography,
  Button,
  Grid
} from '@mui/material';
import { useNavigate } from 'react-router-dom';
import { CartContext } from '../context/CartContext';

const Menu = () => {
  const [menu, setMenu] = useState([]);
  const { addToCart } = useContext(CartContext);
  const navigate = useNavigate();

  useEffect(() => {
    fetch('http://localhost:5001/menu')
      .then((res) => res.json())
      .then((data) => setMenu(data))
      .catch((err) => console.error('Failed to fetch menu:', err));
  }, []);

  const handleAdd = (item) => {
    addToCart(item);
  };

  return (
    <div style={{ padding: 24 }}>
      <Typography variant="h4" gutterBottom style={{ textAlign: 'center', color: '#444' }}>
        PVR Food Menu
      </Typography>
      <Grid container spacing={3}>
        {menu.map((item) => (
          <Grid item xs={12} sm={6} md={4} key={item.id}>
            <Card style={{ background: '#1e1e1e', color: '#fff' }}>
              {item.image && (
                <CardMedia
                  component="img"
                  height="140"
                  image={item.image}
                  alt={item.name}
                />
              )}
              <CardContent>
                <Typography variant="h6">{item.name}</Typography>
                <Typography color="textSecondary">â‚¹{item.price}</Typography>
                <Button
                  fullWidth
                  variant="contained"
                  sx={{ marginTop: '10px' }}
                  onClick={() => handleAdd(item)}
                >
                  Add to Cart
                </Button>
              </CardContent>
            </Card>
          </Grid>
        ))}
      </Grid>

      <div style={{ textAlign: 'center', marginTop: 32 }}>
        <Button
          variant="contained"
          color="secondary"
          onClick={() => navigate('/checkout')}
        >
          Go to Checkout
        </Button>
      </div>
    </div>
  );
};

export default Menu;
